import React, { useState } from "react";
import Modal from "react-modal";
import "../../components/homeContent/becomeInstructor.css";

function BecomeInstructor() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    email: "",
    instructorName: "",
    experience: "",
    mobile: "",
    gender: "",
    password: "",
    status: "",

    email: "",
    password: "",
  });

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const openLoginModal = () => {
    setIsLoginModalOpen(true);
  };

  const closeLoginModal = () => {
    setIsLoginModalOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // handle form submission logic here

    console.log(formData);
    closeModal();
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    // handle login submission logic here

    console.log(formData); // You can replace this with your login logic
    closeLoginModal();
  };

  return (
    <div className="becomeInstructorDiv">
      {/* ... (rest of the existing code) ... */}

      <div className="startTeaching button" onClick={openModal}>
        START TEACHING TODAY
      </div>

      {/* Already have an account link */}
      <div className="login-link" onClick={openLoginModal}>
        Already have an account?
      </div>

      {/* Signup Modal */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Instructor Sign Up"
      >
        {/* ... (existing code for signup modal) ... */}
      </Modal>

      {/* Login Modal */}
      <Modal
        isOpen={isLoginModalOpen}
        onRequestClose={closeLoginModal}
        contentLabel="Instructor Login"
      >
        <div className="modal-content">
          <button className="close-button" onClick={closeLoginModal}>
            X
          </button>
          <div className="contentDiv">
            <h1>Login</h1>
            <form onSubmit={handleLoginSubmit} className="two-inputs-per-row">
              {/* Email and Password fields for login */}
              <div className="form-row">
                <div className="input-group">
                  <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="input-group">
                  <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <button type="submit">Login</button>
            </form>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default BecomeInstructor;
