import React, { useState } from "react";
import Modal from "react-modal";
import "../../components/homeContent/becomeInstructor.css";

const baseURL = "http://localhost:14440/ashram/api/auth/v1";

function BecomeInstructor() {
  
  const [isTeacherSignupModalOpen, setTeacherSignupModalOpen] = useState(false);
  const [formData, setFormData] = useState({
   
    // Fields for Signup
    email: "",
    instructorName: "",
    experience: "",
    mobile: "",
    gender: "",
    password: "",
    status: "",
  });

  const openTeacherSignupModal = () => {
    setSignupModalOpen(true);
  };

  const closeSignupModal = () => {
    setSignupModalOpen(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSignup = (e) => {
    e.preventDefault();
    fetch(`${baseURL}/signup`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.status === 200) {
          setFormData({
            email: "",
            name: "",
            userid: "",
            mobile: "",
            gender: "",
            profession: "",
            address: "",
            password: "",
            resetToken: "",
          });
          console.log("Signup successful");
          closeSignupModal();
        } else {
          console.error("Signup failed");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };


  return (
    <div className="becomeInstructorDiv">
      <div className="backgroundColorDiv"></div>
      <img
        src="https://s.udemycdn.com/home/non-student-cta/udlite-lohp-promo-teacher.jpg"
        alt="instructorImg"
        className="instructorImg"
      ></img>
      <div className="contentDiv">
        <h2 className="heading" >Become an Instructor</h2>


        <p className="about">
          Top instructors from around the world teach millions of students on
          Valmiki. We provide the tools and skills to teach what you love.{" "}
        </p>

        <div className="signup button" onClick={openSignupModal}>
        START  TEACHING TODAY
        </div>

        <Modal
          isOpen={isSignupModalOpen}
          onRequestClose={closeSignupModal}
          contentLabel="Sign Up Modal"
        >
          <div className="signup-popup">
          <button className="close-button" onClick={closeSignupModal}> X </button> 
          <img
          src="../about1.jpeg" 
           alt="instructorImg"
           className="instructorImg"
           style={{ width: "50%", height: "100%", maxWidth: "100%" }}
      ></img>

           <div className="contentDiv">
            <h1>SignUp as Instructor</h1>
            <form onSubmit={handleSignup} className="two-inputs-per-row">
  <div className="form-row">
    <div className="input-group">
      
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="name"
                placeholder="Username"
                value={formData.username}
                onChange={handleInputChange}
                required
              />
               </div>
  </div>
  <div className="form-row">
    <div className="input-group">  
              <input
                type="text"
                name="userid"
                placeholder="UserId"
                value={formData.userid}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="mobile"
                placeholder="Mobile"
                value={formData.mobile}
                onChange={handleInputChange}
                required
              />

</div>
  </div>
  <div className="form-row">
    <div className="input-group">
               <input
                type="text"
                name="gender"
                placeholder="Gender"
                value={formData.gender}
                onChange={handleInputChange}
                required
              />
               <input
                type="text"
                name="profession"
                placeholder="Profession"
                value={formData.profession}
                onChange={handleInputChange}
                required
              />
 </div>
  </div>
  <div className="form-row">
    <div className="input-group">
                <input
                type="text"
                name="address"
                placeholder="Address"
                value={formData.address}
                onChange={handleInputChange}
                required
              />
         </div></div>
         <div className="form-row">
    <div className="input-group">    
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
              /> 

               <input
                type="text"
                name="resetToken"
                placeholder="Reset Token"
                value={formData.resetToken}
                onChange={handleInputChange}
                required
              />
           </div>
  </div>
              <button type="submit">Sign Up</button>
            </form>
          </div>
          </div>
        </Modal>



      </div>
    </div>
  );
}

export default BecomeInstructor;
