import React, { useState } from "react";
import Modal from "react-modal";
import "../../components/homeContent/becomeInstructor.css";

const baseURL = "http://localhost:14440/ashram/api/auth/v1";

function BecomeInstructor() {
  
  const [isTeacherSignupModalOpen, setTeacherSignupModalOpen] = useState(false);
  const [formData, setFormData] = useState({
   
    // Fields for Signup
    email: "",
    instructorName: "",
    experience: "",
    mobile: "",
    gender: "",
    password: "",
    status: "",
  });

  const openTeacherSignupModal = () => {
    setTeacherSignupModalOpen(true);
  };

  const closeTeacherSignupModal = () => {
    setTeacherSignupModalOpen(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleTeacherSignup = (e) => {
    e.preventDefault();
    fetch(`${baseURL}/teacherSignup`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.status === 200) {
          setFormData({
            email: "",
            instructorName: "",
            experience: "",
            mobile: "",
            gender: "",
            password: "",
            status: "",
          });
          console.log("Signup successful");
          closeTeacherSignupModal();
        } else {
          console.error("Signup failed");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };


  return (
    <div className="becomeInstructorDiv">
      <div className="backgroundColorDiv"></div>
      <img
        src="https://s.udemycdn.com/home/non-student-cta/udlite-lohp-promo-teacher.jpg"
        alt="instructorImg"
        className="instructorImg"
      ></img>
      <div className="contentDiv">
        <h2 className="heading" >Become an Instructor</h2>


        <p className="about">
          Top instructors from around the world teach millions of students on
          Valmiki. We provide the tools and skills to teach what you love.{" "}
        </p>

        <div className="signup button" onClick={openTeacherSignupModal}>
        START  TEACHING TODAY
        </div>

        <Modal
          isOpen={isTeacherSignupModalOpen}
          onRequestClose={closeTeacherSignupModal}
          contentLabel="TeacherSign Up Modal"
        >
          <div className="signup-popup">
          <button className="close-button" onClick={closeTeacherSignupModal}> X </button> 
          <img
          src="../about1.jpeg" 
           alt="instructorImg"
           className="instructorImg"
           style={{ width: "50%", height: "100%", maxWidth: "100%" }}
      ></img>

           <div className="contentDiv">
            <h1>SignUp as Instructor</h1>
            <form onSubmit={handleTeacherSignup} className="two-inputs-per-row">
  <div className="form-row">
    <div className="input-group">
      
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              
               </div>
  </div>
  <div className="form-row">
    <div className="input-group"> 
    <input
                type="text"
                name="name"
                placeholder="Instructorname"
                value={formData.instructorname}
                onChange={handleInputChange}
                required
              /> 
              <input
                type="text"
                name="experience"
                placeholder="Experience"
                value={formData.userid}
                onChange={handleInputChange}
                required
              />
              

</div>
  </div>
  <div className="form-row">
    <div className="input-group">
               <input
                type="text"
                name="gender"
                placeholder="Gender"
                value={formData.gender}
                onChange={handleInputChange}
                required
              />
                <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
              /> 
 </div>
  </div>
  <div className="form-row">
    <div className="input-group">
                <input
                type="text"
                name="status"
                placeholder="Status"
                value={formData.address}
                onChange={handleInputChange}
                required
              />
         </div></div>
      
              <button type="submit">Sign Up</button>
            </form>
          </div>
          </div>
        </Modal>



      </div>
    </div>
  );
}

export default BecomeInstructor;
