import React from 'react';
import "../../components/assignment/assignment.css";

const Assignment = () => {
  // Sample data for assignment cards
  const assignments = [
    {
      id: 1,
      title: 'Assignment 1',
      description: 'Explain the OSI model in detail.',
    },
    {
      id: 2,
      title: 'Assignment 2',
      description: 'What is a deadlock? What are the conditions for a deadlock to occur',
    },
    {
      id: 3,
      title: 'Assignment 3',
      description: 'Solve the quadratic equation: ax^2 + bx + c = 0',
    },
    // Add more assignment objects as needed
  ];

  return (
    <div className="assignment-container">
      <h1>Daily Assignments</h1>
      {assignments.map((assignment) => (
        <div key={assignment.id} className="assignment-card">
          <div className="assignment-content">
            <h2>{assignment.title}:</h2>
            <p>{assignment.description}</p>
          </div>
          <button className="attempt-button">Attempt</button>
        </div>
      ))}
    </div>
  );
};

export default Assignment;
