import React, { useState } from "react";
import Modal from "react-modal";
import "../../components/homeContent/becomeInstructor.css";

function BecomeInstructor() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [formData, setFormData] = useState({

     // Fields for Signup
    email: "",
    instructorName: "",
    experience: "",
    mobile: "",
    gender: "",
    password: "",
    status: "",

       // Fields for Login
       email: "", 
       password: "",
  });

  const navigateToLogin = () => {
    setForgotPasswordModalOpen(true);
  };


  const openModal = () => {
    setIsModalOpen(true);
  };

  const openLoginModal = () => {
    setIsLoginModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setIsLoginModalOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // handle form submission logic here

    console.log(formData);
    closeModal();
  };

  return (
    <div className="becomeInstructorDiv">
      {/* ... (your existing content) */}

      
      <div className="login-link" onClick={openLoginModal}>
        Already have an account?
      </div>

      {/* Signup Modal */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Instructor Sign Up"
      >
        {/* ... (your existing signup modal content) */}
      </Modal>

      {/* Login Modal */}
      <Modal
        isOpen={isLoginModalOpen}
        onRequestClose={closeModal}
        contentLabel="Instructor Login"
      >
        <div className="modal-content">
          <button className="close-button" onClick={closeModal}>
            X
          </button>
          <div className="contentDiv">
            <h1>Login as Instructor</h1>
            <form onSubmit={handleLoginSubmit} className="two-inputs-per-row">
              {/* Login form fields */}
              <div className="form-row">
                <div className="input-group">
                  <input
                    type="email"
                    name="loginEmail"
                    placeholder="Email"
                    value={loginFormData.email}
                    onChange={handleLoginChange}
                    required
                  />
                </div>
              </div>
              <div className="form-row">
                <div className="input-group">
                  <input
                    type="password"
                    name="loginPassword"
                    placeholder="Password"
                    value={loginFormData.password}
                    onChange={handleLoginChange}
                    required
                  />
                </div>
              </div>

              <button type="submit">Login</button>
            </form>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default BecomeInstructor;
