import React, { useState } from "react";
import Modal from "react-modal";
import ShoppingCartOutlinedIcon from "@material-ui/icons/ShoppingCartOutlined";
import SearchOutlinedIcon from "@material-ui/icons/SearchOutlined";
import { useNavigate } from 'react-router-dom';
import "../../components/header/headerPrimary.css";

const baseURL = "http://localhost:14440/ashram/api/auth/v1";

function HeaderPrimary() {
  const navigate = useNavigate();
  
  const [isSignupModalOpen, setSignupModalOpen] = useState(false);
  const [isLoginModalOpen, setLoginModalOpen] = useState(false);
  const [isForgotPasswordModalOpen, setForgotPasswordModalOpen] = useState(false); 
  const [formData, setFormData] = useState({
   
    // Fields for Signup
    email: "",
    name: "",
    userid: "",
    mobile: "",
    gender: "",
    profession: "",
    address: "",
    password: "",
    resetToken: "",

    // Fields for Login
    loginEmail: "", // Assuming you use a different email for login
    loginPassword: "",

    // Fields for Forgot Password
    forgotPasswordEmail: "", // Assuming you use a different email for forgot password
 
  });

  const navigateToForgotPassword = () => {
    setForgotPasswordModalOpen(true);
  };

  const openSignupModal = () => {
    setSignupModalOpen(true);
  };

  const closeSignupModal = () => {
    setSignupModalOpen(false);
  };

  const openLoginModal = () => {
    setLoginModalOpen(true);
  };

  const closeLoginModal = () => {
    setLoginModalOpen(false);
  };

  const closeForgotPasswordModal = () => {
    setForgotPasswordModalOpen(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSignup = (e) => {
    e.preventDefault();
    fetch(`${baseURL}/signup`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.status === 200) {
          setFormData({
            email: "",
            name: "",
            userid: "",
            mobile: "",
            gender: "",
            profession: "",
            address: "",
            password: "",
            resetToken: "",
            loginEmail: formData.loginEmail,
            loginPassword: formData.loginPassword,
            forgotPasswordEmail: formData.forgotPasswordEmail,
          });
          console.log("Signup successful");
          closeSignupModal();
        } else {
          console.error("Signup failed");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    fetch(`${baseURL}/signin`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData), 
    })
      .then((response) => {
        if (response.status === 200) {

          setFormData({



            
          })
          console.log("Login successful");
          closeLoginModal();
        } else {
          console.error("Login failed");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const handleResetPassword = (e) => {
    e.preventDefault();
    fetch(`${baseURL}/forgot`, {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: `email=${formData.email}`,
    })
      .then((response) => {
        if (response.status === 200) {
          console.log("Password reset request sent successfully");
          closeForgotPasswordModal();
          
        } else {
          console.error("Password reset request failed");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });

  };

  return (
    <div className="headerPrimary">
      <div className="left part">
       <div><h3>Valmiki</h3></div>
      </div>
      <div className="mid part">
        <div className="searchIcon">
          <SearchOutlinedIcon className="icon" />
        </div>
        <input className="searchBar" placeholder="Search for anything"></input>
      </div>
      <div className="right part">
        <div className="businessDiv" style={{ cursor: "pointer" }}>Coding Platform </div> 
        <div className="teachDiv" style={{ cursor: "pointer" }}>Teach On Valmiki</div>
        <div className="cartDiv">
          <ShoppingCartOutlinedIcon className="icon" />
        </div>
        <div className="login button" onClick={openLoginModal}>
          Login
        </div>

        <Modal
          isOpen={isLoginModalOpen}
          onRequestClose={closeLoginModal}
          contentLabel="Login Modal">

          <div className="login-popup">
          <button className="close-button" onClick={closeLoginModal}> X </button> 

          <img
           src="../about1.jpeg"
           alt="instructorImg"
           className="instructorImg"
             style={{ width: "155%", height: "100%", maxWidth: "100%" }}
      ></img>
          
          <div className="contentDiv">
            <h1>Welcome Back</h1>
            <form onSubmit={handleLogin}>
              
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
              />

              <span
               className="forgot-password-link"
               onClick={navigateToForgotPassword}
               style={{ cursor: "pointer" }}
>
                 Forgot Password?</span>
              <button type="submit">Login</button>
              
            </form>
            </div>
            </div>
        </Modal>

        <div className="signup button" onClick={openSignupModal}>
          Sign up
        </div>

        <Modal
          isOpen={isSignupModalOpen}
          onRequestClose={closeSignupModal}
          contentLabel="Sign Up Modal"
        >
          <div className="signup-popup">
          <button className="close-button" onClick={closeSignupModal}> X </button> 
          <img
          src="../about1.jpeg" 
           alt="instructorImg"
           className="instructorImg"
           style={{ width: "50%", height: "100%", maxWidth: "100%" }}
      ></img>

           <div className="contentDiv">
            <h1>Create Account</h1>
            <form onSubmit={handleSignup} className="two-inputs-per-row">
  <div className="form-row">
    <div className="input-group">
      
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="name"
                placeholder="Username"
                value={formData.username}
                onChange={handleInputChange}
                required
              />
               </div>
  </div>
  <div className="form-row">
    <div className="input-group">  
              <input
                type="text"
                name="userid"
                placeholder="UserId"
                value={formData.userid}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="mobile"
                placeholder="Mobile"
                value={formData.mobile}
                onChange={handleInputChange}
                required
              />

</div>
  </div>
  <div className="form-row">
    <div className="input-group">
               <input
                type="text"
                name="gender"
                placeholder="Gender"
                value={formData.gender}
                onChange={handleInputChange}
                required
              />
               <input
                type="text"
                name="profession"
                placeholder="Profession"
                value={formData.profession}
                onChange={handleInputChange}
                required
              />
 </div>
  </div>
  <div className="form-row">
    <div className="input-group">
                <input
                type="text"
                name="address"
                placeholder="Address"
                value={formData.address}
                onChange={handleInputChange}
                required
              />
         </div></div>
         <div className="form-row">
    <div className="input-group">    
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
              /> 

               <input
                type="text"
                name="resetToken"
                placeholder="Reset Token"
                value={formData.resetToken}
                onChange={handleInputChange}
                required
              />
           </div>
  </div>
              <button type="submit">Sign Up</button>
            </form>
          </div>
          </div>
        </Modal>

        <Modal
          isOpen={isForgotPasswordModalOpen}
          onRequestClose={closeForgotPasswordModal}
          contentLabel="Forgot Password Modal"
        >
          <div className="forgot-password-popup">
          <button className="close-button" onClick={closeForgotPasswordModal}> X </button> 
          <img
          src="../forgot_pass.jpeg" 
           alt="forgotImg"
           className="forgotImg"
           style={{ width: "130%", height: "100%", maxWidth: "100%" }}>
           </img>

            <div className="contentDiv">
            <h1>Forgot Password ?</h1>
            <span
               className="reset">
                Enter the email address associated <br /> with your account</span>
            <form onSubmit={handleResetPassword}>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              
              <button type="submit">Submit</button>
            </form>
          </div>
          </div>
        </Modal>
      </div>
    </div>
  );
}

export default HeaderPrimary;
