import React from 'react';
import "../../components/assignment/footer.css";

const Assignment = () => {
  // Sample data for assignment cards
  const assignments = [
    {
      id: 1,
      title: 'Assignment 1',
      description: 'This is the first assignment for today. Please attempt it.',
    },
    {
      id: 2,
      title: 'Assignment 2',
      description: 'This is the second assignment for today. Remember to submit your solution.',
    },
    {
      id: 3,
      title: 'Assignment 3',
      description: 'This is the third assignment for today. Complete it before the deadline.',
    },
    // Add more assignment objects as needed
  ];

  return (
    <div>
      <h1>Daily Assignments</h1>
      <div className="assignment-container">
        {assignments.map((assignment) => (
          <div key={assignment.id} className="assignment-card">
            <h2>{assignment.title}</h2>
            <p>{assignment.description}</p>
            <button className="attempt-button">Attempt</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Assignment;
